import { takeLatest, put, all, call } from "redux-saga/effects";
import UserActionTypes from "./user.types";
import {
  auth,
  googleProvider,
  createUserProfileDocument
} from "../../firebase/firebase.utils";
import { sSignInSuccess, signInFailure } from "./user.actions";

export function* signInWithGoogle() {
  try {
    // 1. - auth library returns an object that includes a user property which we can use to create userRef
    //    - signInWithPopup (firebase) contains signIn options for several services (sign in with twitter, facebook etc). We just want google
    const { user } = yield auth.signInWithPopup(googleProvider);
    // 2. get userReference from db or create one
    const userRef = yield call(createUserProfileDocument, user);
    // 3. get user snapshot object, which can be used to get the user data
    const userSnapshot = yield userRef.get();
    // 4. dispatch googleSignInSuccess action with user id. Also pass along rest of snapShot data (.data() method gives us actual properties on the snapshot object)
    yield put(
      signInSuccess({
        id: userSnapshot.id,
        ...userSnapshot.data()
      })
    );
  } catch (error) {
    yield put(signInFailure(error));
  }
}

export function* signInWithEmail({ payload: { email, password } }) {
  try {
    const { user } = yield auth.signInWithEmailAndPassword(email, password);
    const userRef = yield call(createUserProfileDocument, user);
    const userSnapshot = yield userRef.get();
    yield put(
      signInSuccess({
        id: userSnapshot.id,
        ...userSnapshot.data()
      })
    );
  } catch (error) {
    yield put(signInFailure(error));
  }
}

// listener effects listen for the SIGN_IN_START action, and then pass action obj into signIn saga
export function* onGoogleSignInStart() {
  yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, signInWithGoogle);
}

export function* onEmailSignInStart() {
  yield takeLatest(UserActionTypes.EMAIL_SIGN_IN_START, signInWithEmail);
}

// instantiate all of the sagas we need to call
export function* userSagas() {
  yield all([call(onGoogleSignInStart), call(onEmailSignInStart)]);
}
